class BaseCLIException(Exception):
    def __init__(self, message: str):
        super().__init__(message)
        self.message = message


class InvalidDatasetNameException(BaseCLIException):
    def __init__(self, message: str):
        super().__init__(message)
        self.type = "invalid_dataset_name"


class NoDatasetException(BaseCLIException):
    def __init__(self, message: str):
        super().__init__(message)
        self.type = "no_dataset_found"


class InvalidConfigurationException(BaseCLIException):
    def __init__(self, message: str):
        super().__init__(message)
        self.type = "invalid_configuration"


class MissingLibraryException(BaseCLIException):
    def __init__(self, message: str):
        super().__init__(message)
        self.type = "missing_library"
